INSERT INTO HAD_VALIDATION_RULE(RULE_ID,CHECK_TABLE,CHECK_FIELD,CHECK_FIELD_NAME,FIELD_VAL,PK_COL_NAME,CONN_TABLE,FK_COL_NAME,CHECK_TYPE,RULE_DESCRIPTION,LOG_SOURCE,ORA_IMPL_STAGE,CHECK_FIELD_DESCRIPTION,CHECK_LEVEL,CHECK_MSG,ORA_MANTAINER) VALUES ('HAD_YW_350_0084','HAD_PA_VALUE','ATTRIBUTE_VALUE','邻接属性','','PA_PID','','','属性检查','非参考线挂接的两条LINK应满足以下条件，否则报LOG： 【1】当LINK属性为“无属性”（即HAD_LINK_FORM.FORM_OF_WAY=1），且该LINK对应序号最大的车道边界的邻接类型邻接属性=-99时，则序号最大的车道边界对应的HAD_PA_VALUE表中邻接属性值应为邻接填充区（即HAD_PA_VALUE.ATTRIBUTE_TYPE=9且ATTRIBUTE_VALUE=1） 【2】当LINK属性为“入口/出口匝道”（即HAD_LINK_FORM.FORM_OF_WAY=98 OR 99），且该LINK对应的序号最小车道边界的邻接类型邻接属性=-99时，则序号最小的车道边界对应的HAD_PA_VALUE表中属性类型等于邻接属性值应为邻接填充区（即HAD_PA_VALUE.ATTRIBUTE_TYPE=9且ATTRIBUTE_VALUE=1）','道路基本属性检查','18M5',2.0,'CRITICAL','主路或出入口匝道的邻接属性错误','余洪涛');
INSERT INTO HAD_VALIDATION_RULE(RULE_ID,CHECK_TABLE,CHECK_FIELD,CHECK_FIELD_NAME,FIELD_VAL,PK_COL_NAME,CONN_TABLE,FK_COL_NAME,CHECK_TYPE,RULE_DESCRIPTION,LOG_SOURCE,ORA_IMPL_STAGE,CHECK_FIELD_DESCRIPTION,CHECK_LEVEL,CHECK_MSG,ORA_MANTAINER) VALUES ('HAD_YW_350_0085','HAD_LINK_SPEEDLIMIT_COND','IS_LANE_DEPENDENT','是否依赖车道限速','','LINK_PID','HAD_LANE_LINK_SPEED_COND','LANE_LINK_PID','属性检查','车道有条件限速（HAD_LANE_LINK_SPEED_COND中LANE_LINK_PID存在条件限速时），该中心线对应的参考线“是否依赖车道限速”应为“是”（HAD_LINK_SPEEDLIMIT中IS_LANE_SPEEDLIMIT=1）,否则报LOG；','道路基本属性检查','18M5',2.0,'FATAL','确定是否勾选依赖车道限速','余洪涛');
